ProbeAction.ScriptBody:

                  (function ($, sccm, data) {
    // BEGIN Discovery(MEF $, WMI sccm, DiscoveryData data)

    var numArgs = $.Arguments.Count();
    var hierarchyId = sccm.SiteId;

    var siteMap = {};
    var currentServerName = "";

    // Find Sites
    var siteQuery = $.Format(" \
    SELECT site.SiteCode, site.Type, site.ReportingSiteCode, site.ServerName, res.ServerRemoteName \n\
      FROM SMS_Site as site JOIN SMS_SystemResourceList as res \n\
        ON res.SiteCode = site.SiteCode AND res.RoleName ='SMS Site Server'");

    sccm.Query(siteQuery, function () {
        var site = this["site"];
        var res = this["res"];
        var siteCode = site.SiteCode;
        var siteType = site.Type;
        var parentSiteCode = site.ReportingSiteCode;
        var serverFqdn;

        if ($.EqualsIgnoreCase(siteCode, sccm.SiteCode)) {
            currentServerName = site.ServerName;
            serverFqdn = sccm.SiteServer;
        }
        else {
            serverFqdn = res.ServerRemoteName;
        }

        siteMap[siteCode] =
        {
            "SiteType": siteType,
            "SiteServerFqdn": serverFqdn,
            "ParentSiteCode": parentSiteCode
        };

    });

    var serverQuery = "\
  SELECT res.ServerRemoteName, res.ServerName, res.SiteCode FROM SMS_SystemResourceList as res \n\
   WHERE res.SiteCode IN ( SELECT SiteCode FROM SMS_Site WHERE Status != 4 AND SiteCode = '" + sccm.SiteCode + "') \n\
     AND res.RoleName ='SMS Site System'";

    // Find all servers under the site code sccm.SiteCode
    sccm.Query(serverQuery, function () {
        var serverDef, serverFqdn, parentSiteCode;
        var siteCode = this.SiteCode;
        var serverName = this.ServerName;

        serverDef = data.CreateClassInstance("$MPElement[Name='MECM.SiteSystemInfo']$");

        if ($.EqualsIgnoreCase(currentServerName, serverName)) {
            serverFqdn = (siteCode == sccm.SiteCode) ? sccm.SiteServer : this.ServerRemoteName;
        }
        else {
            serverFqdn = this.ServerRemoteName;
        }

        serverDef.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", sccm.SiteServer);
        serverDef.AddProperty("$MPElement[Name='MECM.SiteSystemInfo']/ServerName$", serverName);

        data.AddInstance(serverDef);

        parentSiteCode = siteMap[siteCode].ParentSiteCode;

        if ($.IsNullOrEmpty(parentSiteCode) == true) {
            var siteServerFqdn = siteMap[siteCode].SiteServerFqdn;

            // top site may have multiple servers.
            if ($.EqualsIgnoreCase(siteServerFqdn, serverFqdn)) {
                // Here for CAS & Standalone Primary
                var siteType = siteMap[siteCode].SiteType;
                var siteDef;
                var hierarchy = data.CreateClassInstance("$MPElement[Name='MECM.Hierarchy']$");
                hierarchy.AddProperty("$MPElement[Name='MECM.Hierarchy']/HierarchyId$", hierarchyId);
                hierarchy.AddProperty("$MPElement[Name='MECM.Hierarchy']/SiteServer$", serverFqdn);
                hierarchy.AddProperty("$MPElement[Name='MECM.Hierarchy']/SiteCode$", sccm.SiteCode);
                hierarchy.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$", "ConfigMgr - " + sccm.SiteCode);

                data.AddInstance(hierarchy);

                var group = data.CreateClassInstance("$MPElement[Name='MECM.HierarchyComputers.Group']$");
                group.AddProperty("$MPElement[Name='MECM.HierarchyComputers.Group']/HierarchyId$", hierarchyId);
                group.AddProperty("$MPElement[Name='MECM.HierarchyComputers.Group']/SiteServer$", serverFqdn);
                group.AddProperty("$MPElement[Name='MECM.HierarchyComputers.Group']/SiteCode$", sccm.SiteCode);
                group.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$", "ConfigMgr Hierarchy - " + sccm.SiteCode);

                data.AddInstance(group);

                if (siteType == 2) {
                    siteDef = data.CreateClassInstance("$MPElement[Name='MECM.PrimarySite']$");
                }
                else {
                    siteDef = data.CreateClassInstance("$MPElement[Name='MECM.CentralSite']$");
                }

                siteDef.AddProperty("$MPElement[Name='MECM.Site']/SiteServer$", serverFqdn);

                relationship = data.CreateRelationshipInstance("$MPElement[Name='MECM.HierarchyContainsSites']$");
                relationship.Source = hierarchy;
                relationship.Target = siteDef;

                data.AddInstance(siteDef);
                data.AddInstance(relationship);
            }
        }
    });

    // END Discovery(MEF $, WMI sccm, DiscoveryData data)
})($, $("ConfigMgr"), $("DiscoveryData"));


$.Return($("DiscoveryData"));






                  $ = (function ($, undefined) {
   var EXIT = function (code) {
      if (code === undefined) {
         $.Quit(0);
      }
      else {
         $.Quit(code);
      }
   };
   var PRINT = function () {
      var message = arguments[0];
      var length = arguments.length;
      var text = !message ? "" : (!(message.toString) ? "ActiveXObject {...}" : message.toString());

      if (length == 1) {
         $.Echo(text);
      }
      else {
         var i = 1;
         for (; i < length; i++) {
            if ((object = arguments[i]) != null) {
               var append = !object ? "" : (!(object.toString) ? "ActiveXObject {...}" : object.toString());
               $.Echo(text + append);
            }
            else {
               $.Echo(text);
            }
         }
      }
   };
   var ASSERT = function (condition, message, ignore) {
      if (condition === false) {
         PRINT("!! " + message);
         if (!ignore) {
            EXIT(-1);
         }
      }
   };

   var MEF = (function () {
      var toString = Object.prototype.toString;
      var exporters = {};
      var instances = {};
      var class2type = {};

      var MEF = function (selector, context, options) {
         // The MEF object is actually just the init constructor 'enhanced'
         return new MEF.fn.init(selector, context, options);
      };
      var EXPORT = function (name, ctor) {
         exporters[name] = ctor;
         instances[name] = undefined;
      };
      var EXISTS = function (selector) {
         if (!selector) {
            return false;
         }
         else if (typeof selector === "string") {
            var ctor = exporters[selector];
            if (!ctor) {
               return false;
            }
            return true;
         }
         else {
            return false;
         }
      };
      var IMPORT = function (selector, context, options) {
         if (!selector) {
            return undefined;
         }
         else if (typeof selector === "string") {
            var ctor = exporters[selector];
            if (!ctor) {
               if (context == false) {
                  throw selector + " not exported!";
               }
               else {
                  return undefined;
               }
            }
            return ctor.call(options);
         }
         else {
            throw selector + " not supported!";
         }
      };

      MEF.fn = MEF.prototype = {
         Value: function (newVal) {
            if (newVal != undefined) this._Value = newVal;
            return this._Value;
         },
         init: function (selector, options) {
            // global, can only query by string
            if (selector != undefined && typeof selector === "string") {
               var value = instances[selector];
               if (value === undefined) {
                  value = IMPORT(selector, false, this.extend({
                     name: "#" + selector,
                     Value: function () {
                        return this._Value;
                     }
                  }));
                  if (value != undefined) {
                     instances[selector] = value;
                  }
               }
               return value;
            }
            else if (selector != undefined) {
               return MEF.extend(selector, this);
            }
            else {
               return undefined;
            }
         },
         each: function (callback) {
            MEF.ForEach(this.call(this), callback);
         },
         first: function () {
            var retVal;
            MEF.ForEach(this.call(this), function () {
               retVal = this;
               return false
            });
            return retVal;
         },
         to_s: function () {
            var v = this.Value();
            if (v == undefined) {
               return "null";
            }
            else {
               return ":" + v.toString();
            }
         }
      }

      // Give the init function the MEF prototype for later instantiation
      MEF.fn.init.prototype = MEF.fn;

      MEF.extend = MEF.fn.extend = function () {
         var options, name, src, copy, copyIsArray, clone, target = arguments[0] || {},
             i = 1,
             length = arguments.length,
             deep = false;

         // Handle a deep copy situation
         if (typeof target === "boolean") {
            deep = target;
            target = arguments[1] || {};
            // skip the boolean and the target
            i = 2;
         }

         // Handle case when target is a string or something (possible in deep copy)
         if (typeof target !== "object" && !MEF.isFunction(target)) {
            target = {};
         }

         // extend MEF itself if only one argument is passed
         if (length === i) {
            target = this;
            --i;
         }

         for (; i < length; i++) {
            // Only deal with non-null/undefined values
            if ((options = arguments[i]) != null) {
               // Extend the base object
               for (name in options) {
                  src = target[name];
                  copy = options[name];

                  // Prevent never-ending loop
                  if (target === copy) {
                     continue;
                  }

                  // Recurse if we're merging plain objects or arrays
                  if (deep && copy && (MEF.isPlainObject(copy) || (copyIsArray = MEF.isArray(copy)))) {
                     if (copyIsArray) {
                        copyIsArray = false;
                        clone = src && MEF.isArray(src) ? src : [];

                     } else {
                        clone = src && MEF.isPlainObject(src) ? src : {};
                     }

                     // Never move original objects, clone them
                     target[name] = MEF.extend(deep, clone, copy);

                     // Don't bring in undefined values
                  } else if (copy !== undefined) {
                     target[name] = copy;
                  }
               }
            }
         }

         // Return the modified object
         return target;
      };

      MEF.extend({
         version: "1.0.0",
         Arguments: $.Arguments,
         WriteLine: PRINT,
         Assert: ASSERT,
         Exists: EXISTS,
         Export: EXPORT,
         Import: function (selector, options) {
            // context, query by string or undefined
            var val = new MEF.fn.init(undefined);
            return IMPORT(selector, true, val.extend(options));
         },

         Value: function (selector, value) {
            instances[selector]._Value = value;
         },
         // args is for internal usage only
         ForEach: function (object, callback, args) {
            var name, i = 0,
                length = object.length,
                isObj = length === undefined || MEF.isFunction(object),
                isCom = isObj && !object.toString;

            if (args) {
               if (isObj) {
                  for (name in object) {
                     if (callback.apply(object[name], args) === false) {
                        break;
                     }
                  }
               } else {
                  for (; i < length;) {
                     if (callback.apply(object[i++], args) === false) {
                        break;
                     }
                  }
               }

               // A special, fast, case for the most common use of each
            } else {
               if (isCom) {
                  var e = new Enumerator(object);
                  for (; !e.atEnd(); e.moveNext()) {
                     value = e.item();
                     if (callback.call(value, i++, value) === false) {
                        break;
                     }
                  }
               }
               else if (isObj) {
                  for (name in object) {
                     if (callback.call(object[name], name, object[name]) === false) {
                        break;
                     }
                  }
               } else {
                  for (var value = object[0];
                  i < length && callback.call(value, i, value) !== false; value = object[++i]) {}
               }
            }

            return object;
         },

         Dump: function (object) {
            MEF.ForEach(object, function(key, value) {
               if (typeof value !== "object" && !MEF.isFunction(value)) {
                  PRINT("      ", key);
                  PRINT("", value);
               }
            });
         },

         // string helpers
         Trim: function (str, whitespace) {
            if (str == null) {
               return null;
            }
            else if (!whitespace) {
               return str.replace(/^\s\s*/, '').replace(/\s\s*$/, '');
            }
            else {
               for (var i = 0; i < str.length; i++) {
                  if (whitespace.indexOf(str.charAt(i)) === -1) {
                     str = str.substring(i);
                     break;
                  }
               }
               for (i = str.length - 1; i >= 0; i--) {
                  if (whitespace.indexOf(str.charAt(i)) === -1) {
                     str = str.substring(0, i + 1);
                     break;
                  }
               }
               return whitespace.indexOf(str.charAt(0)) === -1 ? str : '';
            }
         },
         EqualsIgnoreCase: function (left, right) {
            if (left == null || right == null) {
               if (left == null && right == null) {
                  return true;
               }
               else {
                  return false;
               }
            }

            return left.toUpperCase() == right.toUpperCase();
         },
         Format: function (frmt, args) {
            for (var x = 0; x < arguments.length; x++) {
               frmt = frmt.replace("{" + x + "}", arguments[x + 1]);
            }

            return frmt;
         },
         IsNullOrEmpty: function (str) {
            if (!str)
            {
               return true;
            }

            return MEF.Trim(str) === "";
         },

         // print helpers
         _: PRINT,
         Return: function (value) {
            PRINT("//-----------------------------------------------------------------------");
            MEF.ForEach(value.toString().split("\n"), function () {
               PRINT("// " + this);
            });
            PRINT("//-----------------------------------------------------------------------");
         },

         // type check
         type: function (obj) {
            return obj == null ? String(obj) : class2type[toString.call(obj)] || "object";
         },
         isFunction: function (obj) {
            return MEF.type(obj) === "function";
         },
         isArray: Array.isArray ||
         function (obj) {
            return MEF.type(obj) === "array";
         },
         isPlainObject: function (obj) {
            // Must be an Object.
            // Because of IE, we also have to check the presence of the constructor property.
            // Make sure that DOM nodes and window objects don't pass through, as well
            if (!obj || MEF.type(obj) !== "object" || obj.nodeType || MEF.isWindow(obj)) {
               return false;
            }

            // Not own constructor property must be Object
            if (obj.constructor && !hasOwn.call(obj, "constructor") && !hasOwn.call(obj.constructor.prototype, "isPrototypeOf")) {
               return false;
            }

            // Own properties are enumerated firstly, so to speed up,
            // if last one is own, then all properties are own.

            var key;
            for (key in obj) {}

            return key === undefined || hasOwn.call(obj, key);
         }
      });

      // Populate the class2type map
      MEF.ForEach("Boolean Number String Function Array Date RegExp Object".split(" "), function (i, name) {
         class2type["[object " + name + "]"] = name.toLowerCase();
      });

      return MEF;
   })();

   return MEF;
})(WScript);

(function ($) {
   $.Export("0", function () {
      this._Value = 0;
      return this;
   });
})($);
(function ($) {
   var HEX = (function (int32) {
      return "0x" + UNSIGNED(int32).toString(16).toUpperCase();
   });
   var UNSIGNED = (function (int32) {
      return 0xFFFFFFFF + int32 + 1;
   });
   var DIAGNOSE = (function (err) {
      if (UNSIGNED(err.number) == 0x8004100E) {
         $.WriteLine("0x8004100E, Error: Invalid namespace");
      }
      else {
         $.WriteLine("Unknown Error: ", HEX(err.number));
      }
   });

   $.Export("WMI", function () {
      // variable "wmi" is requred when creating "WMI.Class" instance
      var wmi = $.extend(function (clazz) {
         if ($.Exists("." + clazz) == false) {
            $.Export("." + clazz, function () {
               return $.Import("WMI.Class", {
                  ClassName: clazz,
                  WMI: wmi
               });
            });
         }

         return $("." + clazz);
      }, this, {
         WmiServer: undefined,
         Connect: function (server, root) {
            var conn = "winmgmts:{impersonationLevel=impersonate,authenticationLevel=pktPrivacy}!";
            if (!server) {
               conn += root;
            }
            else {
               conn += "\\\\" + server + "\\" + root;
            }

            try {
               this.WmiServer = GetObject(conn);
            }
            catch (error) {
               DIAGNOSE(error);
               throw error;
            }

            return this;
         },
         TryConnect: function (server, root) {
            var conn = "winmgmts:{impersonationLevel=impersonate,authenticationLevel=pktPrivacy}!";
            if (!server) {
               conn += root;
            }
            else {
               conn += "\\\\" + server + "\\" + root;
            }

            try {
               this.WmiServer = GetObject(conn);
            }
            catch (error) {
               return undefined;
            }

            return this;
         },
         Query: function (query, callback) {
            var values = this.WmiServer.ExecQuery(query);

            if (values.Count != 0) {
               $.ForEach(values, callback);
               return true;
            }
            else {
               return false;
            }
         }
      });

      return wmi;
   });

   $.Export("WMI.Class", function () {
      this._Value = [];

      return $.extend(function () {
         var values = this.WMI.WmiServer.ExecQuery("SELECT * FROM " + this.ClassName);

         if (values.Count != 0) {
            this._Value = values;
         }
         else {
            this._Value = [];
         }

         return this._Value;
      }, this, {
         Where: function (condition) {
            return $.Import("WMI.Query", {
               WMI: this.WMI,
               ClassName: this.ClassName,
               Condition: condition,
               Sorted: undefined
            });
         },
         OrderBy: function (order) {
            return $.Import("WMI.Query", {
               WMI: this.WMI,
               ClassName: this.ClassName,
               Condition: undefined,
               Sorted: order
            });
         },
         Execute: function (method, input, output) {
            var wmiInput, wmiOutput;
            var wmiClass = this.WMI.WmiServer.Get(this.ClassName);
            var wmiMethod = wmiClass.Methods_.Item(method);

            if (wmiMethod.InParameters != null) {
               wmiInput = input.call(wmiMethod.InParameters.SpawnInstance_());
               wmiOutput = wmiClass.ExecMethod_(wmiMethod.Name, wmiInput);
            }
            else {
               wmiOutput = wmiClass.ExecMethod_(wmiMethod.Name);
            }

            if (output != undefined) {
               return output.call(wmiOutput);
            }
         }
      });
   });

   $.Export("WMI.Query", function () {
      this._Value = [];

      return $.extend(function () {
         var order = (!this.Sorted) ? "" : " ORDER BY " + this.Sorted;
         var where = (!this.Condition) ? "" : " WHERE " + this.Condition;
         var values = this.WMI.WmiServer.ExecQuery("SELECT * FROM " + this.ClassName + order + where);

         if (values.Count != 0) {
            this._Value = values;
         }
         else {
            this._Value = [];
         }

         return this._Value;
      }, this, {
         OrderBy: function (order) {
            var newOrder = (!this.Sorted) ? order : this.Sorted + ", " + order;

            return $.Import("WMI.Query", {
               WMI: this.WMI,
               ClassName: this.ClassName,
               Condition: this.Condition,
               Sorted: newOrder
            });
         }
      });
   });
})($);
(function ($, scriptAPI, undefined) {
    var numArgs = $.Arguments.Count();

    var sourceId = $.Arguments(0);
    var managedEntityId = $.Arguments(1);
    var siteServer = $.Arguments(2);

    $.WriteLine("arguments: ", sourceId, managedEntityId, siteServer);

    $.Export("Mom.ScriptAPI", function () {
        return scriptAPI != undefined ? scriptAPI : WScript.CreateObject("Mom.ScriptAPI");
    });
    $.Export("DiscoveryData", function () {
        return $("Mom.ScriptAPI").CreateDiscoveryData(0, sourceId, managedEntityId);
    });
    $.Return = function (value) {
        $("Mom.ScriptAPI").Return(value);
    };

    $.Export("ConfigMgr", function () {
        var siteCode = $("CIMv2").GetRegistryStringValue("SOFTWARE\\Microsoft\\SMS\\Identification", "Site Code");
        var siteId = $("CIMv2").GetRegistryStringValue("SOFTWARE\\Microsoft\\SMS\\Identification", "Site ID");

        var siteWmi = $.Import("WMI").TryConnect(siteServer, "root\\sms");
        var sdkProv;
        var providerLocation;

        if (siteWmi === undefined) {
            var parentSiteCode = $("CIMv2").GetRegistryStringValue("SOFTWARE\\Microsoft\\SMS\\Identification", "Parent Site Code");
            var parentSiteServer = $("CIMv2").GetRegistryStringValue("SOFTWARE\\Microsoft\\SMS\\Identification", "Parent Server");

            if (parentSiteServer.length === 0) {
                var sqlServer = $("CIMv2").GetRegistryStringValue("SOFTWARE\\Microsoft\\SMS\\SQL Server", "Server");
                var database = $("CIMv2").GetRegistryStringValue("SOFTWARE\\Microsoft\\SMS\\SQL Server", "Database Name");
                // If we are using sql named instance
                if (database.indexOf("\\") !== -1) {
                    var databaseArray = database.split("\\");
                    sqlServer = sqlServer + "\\" + databaseArray[0];
                    database = databaseArray[1];
                }
                var sqlConnection = new ActiveXObject("ADODB.Connection");
                var connectionString = "Data Source=" + sqlServer + ";Initial Catalog=" + database + ";Integrated Security=SSPI;Provider=SQLOLEDB";
                sqlConnection.Open(connectionString);
                var result = new ActiveXObject("ADODB.Recordset");
                result.Open("select TOP(1) ServerRemoteName from sysreslist where RoleName = 'SMS Site Server' and SiteCode = '" + parentSiteCode + "'", sqlConnection);
                result.MoveFirst;
                while (!result.eof) {
                    parentSiteServer = result.fields(0).Value;
                    result.MoveNext;
                }
                result.close;
                sqlConnection.close;
            }

            siteWmi = $.Import("WMI").TryConnect(parentSiteServer, "root\\sms");
            if (siteWmi === undefined) {
                return undefined;
            }

            // now we have connected to parent site's 'root\sms'
            siteWmi("SMS_ProviderLocation").Where("SiteCode = '" + parentSiteCode + "'").each(function () {
                providerLocation = this.Machine;
                sdkProv = $.Import("WMI").Connect(providerLocation, "root\\sms\\site_" + parentSiteCode);
                return false;
            });
        }
        else {
            // now we have connected to 'root\sms'
            siteWmi("SMS_ProviderLocation").Where("SiteCode = '" + siteCode + "'").each(function () {
                providerLocation = this.Machine;
                sdkProv = $.Import("WMI").Connect(providerLocation, "root\\sms\\site_" + siteCode);
                return false;
            });
        }

        return sdkProv.extend({
            SiteCode: siteCode,
            SiteServer: siteServer,
            SiteId: siteId,
            ProviderLocation: providerLocation,
            ResolveServerFqdn: function (serverName, remoteServerName) {
                if (serverName.indexOf(".") > 0) {
                    return serverName;
                }
                else {
                    return remoteServerName;
                }
            }
        });
    });

    $.Export("CIMv2", function () {
        var cimWmi = $.Import("WMI").Connect(siteServer, "root\\cimv2");
        var defaultWmi = $.Import("WMI").Connect(siteServer, "root\\default");

        var HKLM = 0x80000002;

        return cimWmi.extend({
            CurrentServer: siteServer,
            GetRegistryStringValue: function (strKeyPath, strValueName) {

                var value = defaultWmi("StdRegProv").Execute(
                      "GetStringValue",
                      function () {
                          this.sSubKeyName = strKeyPath;
                          this.sValueName = strValueName;

                          return this;
                      },
                      function () {
                          if (this.ReturnValue == 0) {
                              return this.sValue;
                          }
                          else {
                              return undefined;
                          }
                      });

                return value;
            }
        });
    });

})($, $.Import("Mom.ScriptAPI"));
(function ($, undefined) {
   $.Export("ConfigMgr.RoleClassMap", function () {
      var map = {};

      // 0000
      map["Secondary_Site_Server".toUpperCase()] =  "$MPElement[Name='MECM.SecondarySiteServer']$";
      map["Primary_Site_Server".toUpperCase()] =  "$MPElement[Name='MECM.StandaloneSiteServer']$";
      map["Peer_Site_Server".toUpperCase()] =  "$MPElement[Name='MECM.PeerSiteServer']$";
      map["Central_Site_Server".toUpperCase()] =  "$MPElement[Name='MECM.CentralSiteServer']$";

      map["ConfigMgr_SDK_Provider".toUpperCase()] =  "$MPElement[Name='MECM.SDKProvider']$";
      map["SMS_Site_Component_Manager".toUpperCase()] =  "$MPElement[Name='MECM.SiteComponentManager']$";

      // 01-05
      map["SMS_ASYNC_RAS_SENDER".toUpperCase()] = undefined;
      map["SMS_Client_Access_Point".toUpperCase()] = undefined;
      map["SMS_Component_Server".toUpperCase()] = "$MPElement[Name='MECM.ComponentServer']$";
      map["SMS_Device_Management_Point".toUpperCase()] = "$MPElement[Name='MECM.DeviceManagementPoint']$";
      map["SMS_Distribution_Point".toUpperCase()] = "$MPElement[Name='MECM.DistributionPoint']$";

      // 06-10
      map["SMS_Fallback_Status_Point".toUpperCase()] = "$MPElement[Name='MECM.FallbackStatusPoint']$";
      map["SMS_LAN_SENDER".toUpperCase()] = undefined;
      map["SMS_Management_Point".toUpperCase()] = "$MPElement[Name='MECM.ManagementPoint']$";
      map["SMS_PXE_Service_Point".toUpperCase()] = "$MPElement[Name='MECM.PXEServicePoint']$";
      map["SMS_RAS_SENDER".toUpperCase()] = undefined;

      // 11-15
      map["SMS_Server_Locator_Point".toUpperCase()] = undefined;
      map["SMS_Site_Server".toUpperCase()] = undefined;
      map["SMS_Site_System".toUpperCase()] = undefined;
      map["SMS_SQL_Server".toUpperCase()] = "$MPElement[Name='MECM.SQLServer']$";
      map["SMS_State_Migration_Point".toUpperCase()] = "$MPElement[Name='MECM.StateMigrationPoint']$";

      // 16-20
      map["SMS_System_Health_Validator".toUpperCase()] = "$MPElement[Name='MECM.SystemHealthValidator']$";
      map["SMS_Software_Update_Point".toUpperCase()] = "$MPElement[Name='MECM.SoftwareUpdatePoint']$";
