// Script below this line is the customized discovery logic
// --------------------------------------------------------

$Config/ScriptBody$


                  (function ($, sccm, data) {
    // BEGIN Discovery(MEF $, WMI sccm, DiscoveryData data)

    var roleClassMap = $("ConfigMgr.RoleClassMap");
    var roleDisplayName = $("ConfigMgr.RoleDisplayName");

    var numArgs = $.Arguments.Count();

    var hierarchyId = sccm.SiteId;
    var targetSiteCode = sccm.SiteCode;

    var targetServerName = numArgs > 3 ? $.Arguments(3) : undefined;

    var siteMap = {};
    var currentServerName = "";

    // Find Sites
    var siteQuery = $.Format(" \
    SELECT site.SiteCode, site.Type, site.ReportingSiteCode, site.ServerName, res.ServerRemoteName \n\
      FROM SMS_Site as site JOIN SMS_SystemResourceList as res \n\
        ON res.SiteCode = site.SiteCode AND res.RoleName ='SMS Site Server'");

    sccm.Query(siteQuery, function () {
        var site = this["site"];
        var res = this["res"];
        var siteCode = site.SiteCode;
        var siteType = site.Type;
        var parentSiteCode = site.ReportingSiteCode;
        var serverName = this.ServerName;
        var serverFqdn;

        if ($.EqualsIgnoreCase(siteCode, sccm.SiteCode)) {
            currentServerName = site.ServerName;
            serverFqdn = sccm.SiteServer;
        }
        else {
            serverFqdn = res.ServerRemoteName;
        }

        siteMap[siteCode] =
        {
            "SiteType": siteType,
            "SiteServerFqdn": serverFqdn,
            "ParentSiteCode": parentSiteCode
        };

    });

    var targetServerFqdn = targetServerName;
    var targetSiteType = siteMap[targetSiteCode].SiteType;
    var siteServerFqdn = siteMap[targetSiteCode].SiteServerFqdn;
    var parentSiteCode = siteMap[targetSiteCode].ParentSiteCode;
    var parentSiteType, parentSiteFqdn;

    var serverQuery = "\
  SELECT res.ServerRemoteName, res.ServerName, res.SiteCode FROM SMS_SystemResourceList as res \n\
   WHERE res.SiteCode IN ( SELECT SiteCode FROM SMS_Site WHERE Status != 4 AND SiteCode = '" + targetSiteCode + "') \n\
     AND ServerName = '" + targetServerName + "'\n\
     AND res.RoleName ='SMS Site System'";

    // Find all servers under the site code sccm.SiteCode
    sccm.Query(serverQuery, function () {
        // Complete the SiteSystemInfo here instead of in hierarchy.js
        var siteSystemDef, serverFqdn;

        siteSystemDef = data.CreateClassInstance("$MPElement[Name='MECM.SiteSystemInfo']$");
        if ($.EqualsIgnoreCase(currentServerName, targetServerName)) {
            serverFqdn = (targetSiteCode == sccm.SiteCode) ? sccm.SiteServer : this.ServerRemoteName;
        }
        else {
            serverFqdn = this.ServerRemoteName;
        }
        targetServerFqdn = $.Trim(serverFqdn, "/\\").toUpperCase();
        siteSystemDef.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", sccm.SiteServer);
        siteSystemDef.AddProperty("$MPElement[Name='MECM.SiteSystemInfo']/ServerName$", targetServerName);
        siteSystemDef.AddProperty("$MPElement[Name='MECM.SiteSystemInfo']/ServerRemoteName$", targetServerFqdn);
        siteSystemDef.AddProperty("$MPElement[Name='MECM.SiteSystemInfo']/SiteCode$", targetSiteCode);
        siteSystemDef.AddProperty("$MPElement[Name='MECM.SiteSystemInfo']/SiteType$", targetSiteType);
        siteSystemDef.AddProperty("$MPElement[Name='MECM.SiteSystemInfo']/SiteServerFqdn$", siteServerFqdn);
        siteSystemDef.AddProperty("$MPElement[Name='MECM.SiteSystemInfo']/HierarchyId$", hierarchyId);

        if ($.IsNullOrEmpty(parentSiteCode) == false) {
            parentSiteType = siteMap[parentSiteCode].SiteType;
            parentSiteFqdn = siteMap[parentSiteCode].SiteServerFqdn;
            siteSystemDef.AddProperty("$MPElement[Name='MECM.SiteSystemInfo']/ParentSiteCode$", parentSiteCode);
            siteSystemDef.AddProperty("$MPElement[Name='MECM.SiteSystemInfo']/ParentSiteType$", parentSiteType);
            siteSystemDef.AddProperty("$MPElement[Name='MECM.SiteSystemInfo']/ParentSiteFqdn$", parentSiteFqdn);
        }
        else {
            parentSiteCode = 0;
            siteSystemDef.AddProperty("$MPElement[Name='MECM.SiteSystemInfo']/ParentSiteType$", parentSiteCode);
        }
        data.AddInstance(siteSystemDef);
    });


    var serverDef, siteDef;
    var isSiteServer = false;

    var serverQuery = $.Format("\
   SELECT res.ServerRemoteName, res.ServerName FROM SMS_SystemResourceList as res \n\
    WHERE res.SiteCode ='{0}' AND res.RoleName ='SMS Site Server'", targetSiteCode);

    // Add SiteServer & RemoteServers
    sccm.Query(serverQuery, function () {
        var siteServerName = this.ServerName;

        if ($.EqualsIgnoreCase(targetServerName, siteServerName)) {
            serverDef = data.CreateClassInstance("$MPElement[Name='MECM.SiteServer']$");
            isSiteServer = true;
        }
        else {
            serverDef = data.CreateClassInstance("$MPElement[Name='MECM.SiteSystemServer']$");
            isSiteServer = false;
        }
    });

    serverDef.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", targetServerFqdn);
    serverDef.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$", targetServerName);
    serverDef.AddProperty("$MPElement[Name='MECM.Server']/HierarchyId$", hierarchyId);
    serverDef.AddProperty("$MPElement[Name='MECM.Server']/SiteCode$", targetSiteCode);

    data.AddInstance(serverDef);

    // Add server to hierarchy group
    var computer = data.CreateClassInstance("$MPElement[Name='Windows!Microsoft.Windows.Computer']$");
    computer.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", targetServerFqdn);

    var group = data.CreateClassInstance("$MPElement[Name='MECM.HierarchyComputers.Group']$");
    group.AddProperty("$MPElement[Name='MECM.HierarchyComputers.Group']/HierarchyId$", hierarchyId);

    data.AddInstance(group);
    data.AddInstance(computer);

    //relationship = data.CreateRelationshipInstance("$MPElement[Name='SC!Microsoft.SystemCenter.ComputerGroupContainsComputer']$");
    //relationship.Source = group;
    //relationship.Target = computer;
    //data.AddInstance(relationship);

    // Create SiteInstance
    switch (targetSiteType) {
        case 1:
            siteDef = data.CreateClassInstance("$MPElement[Name='MECM.SecondarySite']$");
            siteDef.AddProperty("$MPElement[Name='MECM.Site']/SiteTypeName$", "Secondary Site");
            break;
        case 2:
            siteDef = data.CreateClassInstance("$MPElement[Name='MECM.PrimarySite']$");
            siteDef.AddProperty("$MPElement[Name='MECM.Site']/SiteTypeName$", "Primary Site");
            break;
        case 4:
        case 8:
            siteDef = data.CreateClassInstance("$MPElement[Name='MECM.CentralSite']$");
            siteDef.AddProperty("$MPElement[Name='MECM.Site']/SiteTypeName$", "Central Site");
            break;
    }

    siteDef.AddProperty("$MPElement[Name='MECM.Site']/SiteServer$", siteServerFqdn);
    siteDef.AddProperty("$MPElement[Name='MECM.Site']/HierarchyId$", hierarchyId);

    relationship = data.CreateRelationshipInstance("$MPElement[Name='MECM.SiteContainsServers']$");
    relationship.Source = siteDef;
    relationship.Target = serverDef;

    data.AddInstance(siteDef);
    data.AddInstance(relationship);

    // Child Site Discovery
    if (isSiteServer == true) {
        relationship = data.CreateRelationshipInstance("$MPElement[Name='MECM.HierarchyGroupContainsSites']$");
        relationship.Source = group;
        relationship.Target = siteDef;
        data.AddInstance(relationship);

        var parentSite, relationship;
        switch (parentSiteType) {
            case 0:
                // no parent
                break;
            case 2:
                // parent is primary
                parentSite = data.CreateClassInstance("$MPElement[Name='MECM.PrimarySite']$");
                parentSite.AddProperty("$MPElement[Name='MECM.Site']/SiteServer$", parentSiteFqdn);

                relationship = data.CreateRelationshipInstance("$MPElement[Name='MECM.PrimarySiteContainsSecondarySite']$")
                relationship.Source = parentSite;
                relationship.Target = siteDef;
                data.AddInstance(parentSite);
                data.AddInstance(relationship);
                break;
            case 4:
            case 8:
                // parent is central site
                parentSite = data.CreateClassInstance("$MPElement[Name='MECM.CentralSite']$");
                parentSite.AddProperty("$MPElement[Name='MECM.Site']/SiteServer$", parentSiteFqdn);

                relationship = data.CreateRelationshipInstance("$MPElement[Name='MECM.CentralSiteContainsPrimarySite']$")
                relationship.Source = parentSite;
                relationship.Target = siteDef;
                data.AddInstance(parentSite);
                data.AddInstance(relationship);
                break;
            default:
                break;
        }

        // add site component manager
        var roleName = "SMS Site Component Manager";
        var displayName = roleDisplayName(roleName);
        var roleClass = roleClassMap(roleName);

        sitecomp = data.CreateClassInstance(roleClass);
        sitecomp.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", targetServerFqdn);
        sitecomp.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$", displayName + " - " + targetServerName);
        sitecomp.AddProperty("$MPElement[Name='MECM.SiteRole']/RoleName$", roleName);
        sitecomp.AddProperty("$MPElement[Name='MECM.SiteRole']/SiteCode$", targetSiteCode);

        data.AddInstance(sitecomp);

        relationship = data.CreateRelationshipInstance("$MPElement[Name='MECM.ServersContainsSiteRoles']$")
        relationship.Source = serverDef;
        relationship.Target = sitecomp;

        data.AddInstance(relationship);

        switch (targetSiteType) {
            case 1:
                roleName = "Secondary Site Server";

                break;
            case 2:
                // primary
                roleName = "Primary Site Server";
                break;
            case 4:
            case 8:
                roleName = "Central Site Server";
                break;
            default:
                roleName = "";
                break;
        }

        displayName = roleDisplayName(roleName);
        roleClass = roleClassMap(roleName);

        if (roleClass != undefined) {
            sitecomp = data.CreateClassInstance(roleClass);
            sitecomp.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", targetServerFqdn);
            sitecomp.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$", displayName + " - " + targetServerName);
            sitecomp.AddProperty("$MPElement[Name='MECM.SiteRole']/RoleName$", roleName);
            sitecomp.AddProperty("$MPElement[Name='MECM.SiteRole']/SiteCode$", targetSiteCode);

            data.AddInstance(sitecomp);

            relationship = data.CreateRelationshipInstance("$MPElement[Name='MECM.ServersContainsSiteRoles']$")
            relationship.Source = serverDef;
            relationship.Target = sitecomp;

            data.AddInstance(relationship);
        }
    }

    // Site Roles Discovery
    var roleQuery = $.Format("\
   SELECT res.RoleName, res.ServerRemoteName, res.ServerName, res.NALPath, cfg.Props, \n\
          cfg.FileType, cfg.ItemName, cfg.ItemType, cfg.SiteCode from SMS_SystemResourceList as res \n\
    JOIN SMS_SCI_SysResUse as cfg ON cfg.NALPath = res.NALPath and cfg.RoleName = res.RoleName \n\
    WHERE res.SiteCode ='{0}' \n\
      AND res.RoleName <> 'SMS Site System' \n\
      AND res.RoleName <> 'SMS Site Server' \n\
      AND res.ServerName = '{1}'", targetSiteCode, targetServerName);

    sccm.Query(roleQuery, function () {
        var cfg = this["cfg"];
        var res = this["res"];
        var roleDef, relationship, linkDef, sourceDef, serviceDef;
        var roleName = res.RoleName;
        var displayName = roleDisplayName(roleName);
        var roleClass = roleClassMap(roleName);

        if (roleClass != undefined) {
            roleDef = data.CreateClassInstance(roleClass);
            roleDef.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", targetServerFqdn);
            roleDef.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$", displayName + " - " + targetServerName);
            roleDef.AddProperty("$MPElement[Name='MECM.SiteRole']/RoleName$", roleName);
            roleDef.AddProperty("$MPElement[Name='MECM.SiteRole']/SiteCode$", targetSiteCode);

            if ($.EqualsIgnoreCase(roleName, "SMS SQL Server")) {
                var backupServiceName = "SMS_SITE_SQL_BACKUP";

                if (isSiteServer == false) {
                    backupServiceName += "_" + siteServerFqdn;
                }

                roleDef.AddProperty("$MPElement[Name='MECM.SQLServer']/BackupServiceName$", backupServiceName);
            }

            data.AddInstance(roleDef);

            relationship = data.CreateRelationshipInstance("$MPElement[Name='MECM.ServersContainsSiteRoles']$")
            relationship.Source = serverDef;
            relationship.Target = roleDef;

            data.AddInstance(relationship);
        }

        // Management Point
        if ($.EqualsIgnoreCase(roleName, "SMS Management Point")) {
            relationship = data.CreateRelationshipInstance("$MPElement[Name='MECM.SiteContainsManagementPoints']$")
            relationship.Source = siteDef;
            relationship.Target = roleDef;

            data.AddInstance(relationship);


            roleDef = data.CreateClassInstance("$MPElement[Name='MECM.BGBServer']$");
            roleDef.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", targetServerFqdn);
            roleDef.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$", "ConfigMgr Notification Server" + " - " + targetServerName);
            roleDef.AddProperty("$MPElement[Name='MECM.SiteRole']/RoleName$", "BGB Server");
            roleDef.AddProperty("$MPElement[Name='MECM.SiteRole']/SiteCode$", targetSiteCode);
            data.AddInstance(roleDef);

            relationship = data.CreateRelationshipInstance("$MPElement[Name='MECM.ServersContainsSiteRoles']$")
            relationship.Source = serverDef;
            relationship.Target = roleDef;
            data.AddInstance(relationship);

        }
            // Distribution Point
        else if ($.EqualsIgnoreCase(roleName, "SMS Distribution Point")) {
            var nalPath = res.NALPath;
            var serverFqdn = sccm.ResolveServerFqdn(res.ServerName, res.ServerRemoteName);

            // add site contains content distribution 
            linkDef = data.CreateClassInstance("$MPElement[Name='MECM.ContentDistribution']$");
            linkDef.AddProperty("$MPElement[Name='MECM.ContentDistribution']/DistributionPoint$", serverFqdn);

            data.AddInstance(linkDef);

            // site.Add(ContentDistribution)
            relationship = data.CreateRelationshipInstance("$MPElement[Name='MECM.SiteContainsDistributionPoints']$")
            relationship.Source = siteDef;
            relationship.Target = linkDef;

            data.AddInstance(relationship);

            // add content distribution from distribution source
            sourceDef = data.CreateClassInstance("$MPElement[Name='MECM.DistributionSource']$");
            sourceDef.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", siteServerFqdn);
            sourceDef.AddProperty("$MPElement[Name='MECM.DistributionSource']/DistributionPoint$", serverFqdn);
            sourceDef.AddProperty("$MPElement[Name='MECM.DistributionSource']/ServerNALPath$", nalPath);

            data.AddInstance(sourceDef);

            // ContentDistribution.Add(DistributionSource)
            relationship = data.CreateRelationshipInstance("$MPElement[Name='MECM.ContentDistributionFromDistributionSource']$")
            relationship.Source = linkDef;
            relationship.Target = sourceDef;

            data.AddInstance(relationship);

            // ContentDistribution.Add(DistributionPoint)
            relationship = data.CreateRelationshipInstance("$MPElement[Name='MECM.ContentDistributionToDistributionPoint']$")
            relationship.Source = linkDef;
            relationship.Target = roleDef;

            data.AddInstance(relationship);

            var isPxe = false;

            if(cfg["Props"] == null){
                cfg.Refresh_();
            }

            $.ForEach(cfg["Props"].toArray(), function () {
                if ($.EqualsIgnoreCase(this.PropertyName, "IsPXE") &&
                    (this.Value === 1)) {
                    isPxe = true;
                }
            });

            if (isPxe != false) {
                roleName = "SMS PXE Service Point";
                displayName = roleDisplayName(roleName);
                roleClass = roleClassMap(roleName);

                roleDef = data.CreateClassInstance(roleClass);
                roleDef.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", targetServerFqdn);
                roleDef.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$", displayName + " - " + targetServerName);
                roleDef.AddProperty("$MPElement[Name='MECM.SiteRole']/RoleName$", roleName);
                roleDef.AddProperty("$MPElement[Name='MECM.SiteRole']/SiteCode$", targetSiteCode);

                data.AddInstance(roleDef);

                relationship = data.CreateRelationshipInstance("$MPElement[Name='MECM.ServersContainsSiteRoles']$")
                relationship.Source = serverDef;
                relationship.Target = roleDef;

                data.AddInstance(relationship);
            }
        }
            // State Migration Point
        else if ($.EqualsIgnoreCase(roleName, "SMS State Migration Point")) {
            cfg.Refresh_();

            var propList = cfg["PropLists"];

            $.ForEach(propList.toArray(), function () {
                if ($.EqualsIgnoreCase(this.PropertyListName, "Directories")) {
                    var values = this["Values"];
                    var driveList = {};

                    // values looks like 
                    //    "Directory=C:\\;MaxClients=100;MinDiskSpace=100;MinDiskSpaceUnit=1;",
                    //    "Directory=D:\\;MaxClients=100;MinDiskSpace=100;MinDiskSpaceUnit=1;"
                    $.ForEach(values.toArray(), function () {
                        var drive = this;
                        var pattern = "Directory="
                        var left = drive.indexOf(pattern);

                        if (left >= 0) {
                            drive = drive.substr(left + pattern.length);
                            var right = drive.indexOf(";");

                            if (right > 0) {
                                drive = drive.substr(0, right);
                            }

                            // We only C: D:, no /\
                            var regEx = new RegExp("^[a-zA-Z]:");

                            var match = regEx.exec(drive);
                            if (match == null) {
                                drive = "";
                            } else if (match.length > 0) {
                                drive = match[0].toUpperCase();
                            } else {
                                drive = "";
                            }

                            if ($.IsNullOrEmpty(drive) == false &&
                               driveList[drive] === undefined) {
                                var drvDef = data.CreateClassInstance("$MPElement[Name='MECM.ContentStorageDrive']$");
                                drvDef.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", targetServerFqdn);
                                drvDef.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.LogicalDevice']/DeviceID$", drive);

                                data.AddInstance(drvDef);

                                // to ignore duplicate entries
                                driveList[drive] = drvDef;

                                relationship = data.CreateRelationshipInstance("$MPElement[Name='MECM.SiteRoleContainsContentStorage']$")
                                relationship.Source = roleDef;
                                relationship.Target = drvDef;

                                data.AddInstance(relationship);
                            }
                        }
                    });
                }
            });
        }
    });

    var componentClassNames = $("ConfigMgr.ComponentClassNames");
    var componentDisplayNames = $("ConfigMgr.ComponentDisplayNames");

    // List components on this server
    var serverQuery = $.Format("\
   SELECT comp.MachineName, comp.ComponentName FROM SMS_ComponentSummarizer as comp  \n\
    WHERE TallyInterval='0001128000100008'  \n\
      AND comp.MachineName ='{0}'", targetServerName);

    // Add SiteServer & RemoteServers
    sccm.Query(serverQuery, function () {
        var component;
        var componentName = this.ComponentName;
        var displayName = componentDisplayNames(componentName);
        var className = componentClassNames(componentName);

        if (className != undefined) {
            component = data.CreateClassInstance(className);
            component.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", targetServerFqdn);
            component.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$", displayName + " - " + targetServerName);
            component.AddProperty("$MPElement[Name='MECM.ServerComponent']/Name$", componentName);

            data.AddInstance(component);

            relationship = data.CreateRelationshipInstance("$MPElement[Name='MECM.ServersContainsComponents']$")
            relationship.Source = serverDef;
            relationship.Target = component;

            data.AddInstance(relationship);
        }
    });

    // END Discovery(MEF $, WMI sccm, DiscoveryData data)
})($, $("ConfigMgr"), $("DiscoveryData"));


$.Return($("DiscoveryData"));


                  (function ($, cim, data) {
// BEGIN Discovery(MEF $, WMI sccm, DiscoveryData data)

var roleClassMap = $("ConfigMgr.RoleClassMap");

var numArgs = $.Arguments.Count();
// ParseToInt
var targetServerFqdn = cim.CurrentServer;

var roleName = "SMS_Distribution_Point";
var roleClass = roleClassMap(roleName);

if (roleClass != undefined) {
   var roleDef = data.CreateClassInstance(roleClass);
   roleDef.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", targetServerFqdn);

   data.AddInstance(roleDef);

   var regPath = "SOFTWARE\\Microsoft\\SMS\\DP";
   var regKey = "ContentLibUsableDrives";

   // service query
   var contentPath = cim.GetRegistryStringValue(regPath, regKey);

   if ($.IsNullOrEmpty(contentPath) == false)
   {
      var relationship;
      var driveList = {};

      $.ForEach(contentPath.split(","), function () {
         var drive = $.Trim(this);

         var regEx = new RegExp("^[a-zA-Z]:");

         var match = regEx.exec(drive);
         if (match == null) {
            drive = "";
         } else if (match.length > 0) {
            drive = match[0].toUpperCase();
         } else {
            drive = "";
         }

         if ($.IsNullOrEmpty(drive) == false &&
            driveList[drive] === undefined)
         {
            var drvDef = data.CreateClassInstance("$MPElement[Name='MECM.ContentStorageDrive']$");
            drvDef.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", targetServerFqdn);
            drvDef.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.LogicalDevice']/DeviceID$", drive);

            data.AddInstance(drvDef);

            // to ignore duplicate entries
            driveList[drive] = drvDef;

            relationship = data.CreateRelationshipInstance("$MPElement[Name='MECM.SiteRoleContainsContentStorage']$")
            relationship.Source = roleDef;
            relationship.Target = drvDef;

            data.AddInstance(relationship);
         }
      });
   }
}

// END Discovery(MEF $, WMI sccm, DiscoveryData data)
})($, $("CIMv2"), $("DiscoveryData"));


$.Return($("DiscoveryData"));


                  (function ($, cim, data) {
// BEGIN Discovery(MEF $, WMI sccm, DiscoveryData data)

var roleClassMap = $("ConfigMgr.RoleClassMap");

var numArgs = $.Arguments.Count();
// ParseToInt
var serviceName   = numArgs > 3 ? $.Arguments(3) : undefined;
var roleName   = numArgs > 4 ? $.Arguments(4) : undefined;
var targetServerFqdn = cim.CurrentServer;

var roleClass = roleClassMap(roleName);

if (roleClass != undefined) {
   var roleDef = data.CreateClassInstance(roleClass);
   roleDef.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", targetServerFqdn);

   data.AddInstance(roleDef);

   // service query
   var serviceQuery = $.Format("\
      SELECT Name, DisplayName from Win32_Service \n\
       WHERE Name='{0}'", serviceName);

   cim.Query(serviceQuery, function () {

      var svcDef = data.CreateClassInstance("$MPElement[Name='MECM.SystemService']$");
      svcDef.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", targetServerFqdn);
      svcDef.AddProperty("$MPElement[Name='MECM.SystemService']/ServiceName$", serviceName);
      svcDef.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$", this.DisplayName);

      data.AddInstance(svcDef);

      var relationship = data.CreateRelationshipInstance("$MPElement[Name='MECM.SiteRoleContainsSystemService']$")
      relationship.Source = roleDef;
      relationship.Target = svcDef;

      data.AddInstance(relationship);
   });
}

// END Discovery(MEF $, WMI sccm, DiscoveryData data)
})($, $("CIMv2"), $("DiscoveryData"));


$.Return($("DiscoveryData"));




                  (function ($, sccm, data) {
// BEGIN Discovery(MEF $, WMI sccm, DiscoveryData data)

var numArgs = $.Arguments.Count();
// ParseToInt
var siteType   = numArgs > 4 ? parseInt($.Arguments(4)) : undefined;

var siteDef, relationship;

// Create SiteInstance
// this siteType come from registry (1:Primary, 2:Secondary, 8:CAS)
switch (siteType) {
   case 1:
      siteDef = data.CreateClassInstance("$MPElement[Name='MECM.PrimarySite']$");
      break;

   case 2:
      siteDef = data.CreateClassInstance("$MPElement[Name='MECM.SecondarySite']$");

      break;
   case 4:
   case 8:
      siteDef = data.CreateClassInstance("$MPElement[Name='MECM.CentralSite']$");
      break;
}
siteDef.AddProperty("$MPElement[Name='MECM.Site']/SiteServer$", sccm.SiteServer);

// Find ChildSites
sccm("SMS_Site").Where("Status != 4 AND ReportingSiteCode = '" + sccm.SiteCode + "'").each(function () {
   var endPoint;
   var siteCode = this.SiteCode;
   var parentSite = this.ReportingSiteCode;
   var serverFqdn = sccm.SiteServer;
   var isSP1 = this.buildnumber > 7711? "1" : "0";
   //var isSP1 = "true";


   switch (this.Type) {
   case 1:
      endPoint = data.CreateClassInstance("$MPElement[Name='MECM.ReplicationToSecondarySite']$");
      break;
   case 2:
      endPoint = data.CreateClassInstance("$MPElement[Name='MECM.ReplicationToPrimarySite']$");
      break;
   }

   // Display Name
   endPoint.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$", sccm.SiteCode + " - " + siteCode);
   // Source Site
   endPoint.AddProperty("$MPElement[Name='MECM.Replication']/SiteCode$", sccm.SiteCode);
   // Destination Site
   endPoint.AddProperty("$MPElement[Name='MECM.Replication']/TargetSiteCode$", siteCode);
   // Site Server FQDN
   endPoint.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", $.Trim(serverFqdn, "/\\").toUpperCase());   
   // Is Site SP1 or not
   endPoint.AddProperty("$MPElement[Name='MECM.Replication']/IsSP1$", isSP1);   

   data.AddInstance(endPoint);

   relationship = data.CreateRelationshipInstance("$MPElement[Name='MECM.SiteContainsReplication']$")
   relationship.Source = siteDef;
   relationship.Target = endPoint;

   data.AddInstance(relationship);
});

// Find ParentSite
sccm("SMS_Site").Where("SiteCode = '" + sccm.SiteCode + "'").each(function () {
   var endPoint;
   var siteCode = this.SiteCode;
   var parentSite = this.ReportingSiteCode;
   var serverFqdn = sccm.SiteServer;
   var isSP1 = this.buildnumber > 7711? "1" : "0";
   //var isSP1 = "true";

   if (parentSite != "")
   {
      switch (this.Type) {
      case 1:
         endPoint = data.CreateClassInstance("$MPElement[Name='MECM.ReplicationToPrimarySite']$");
         break;
      case 2:
         endPoint = data.CreateClassInstance("$MPElement[Name='MECM.ReplicationToCentralSite']$");
         break;
      }

      // Display Name
      endPoint.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$", sccm.SiteCode + " - " + parentSite);
      // Source Site
      endPoint.AddProperty("$MPElement[Name='MECM.Replication']/SiteCode$", sccm.SiteCode);
      // Destination Site
      endPoint.AddProperty("$MPElement[Name='MECM.Replication']/TargetSiteCode$", parentSite);
      // Site Server FQDN
      endPoint.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", $.Trim(serverFqdn, "/\\").toUpperCase());   
      // Is Site SP1 or not
      endPoint.AddProperty("$MPElement[Name='MECM.Replication']/IsSP1$", isSP1);   

      data.AddInstance(endPoint);

      relationship = data.CreateRelationshipInstance("$MPElement[Name='MECM.SiteContainsReplication']$")
      relationship.Source = siteDef;
      relationship.Target = endPoint;

      data.AddInstance(relationship);
   }
});

// Add provider location for monitoring
var serverDef = data.CreateClassInstance("$MPElement[Name='MECM.SiteServer']$");
var serverFqdn = sccm.SiteServer;
serverDef.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", $.Trim(serverFqdn, "/\\").toUpperCase());
serverDef.AddProperty("$MPElement[Name='MECM.SiteServer']/ProviderLocation$", sccm.ProviderLocation);
data.AddInstance(serverDef);

// END Discovery(MEF $, WMI sccm, DiscoveryData data)
})($, $("ConfigMgr"), $("DiscoveryData"));
(function ($, sccm, data) {
// BEGIN Discovery(MEF $, WMI sccm, DiscoveryData data)

var roleClassMap = $("ConfigMgr.RoleClassMap");
var roleDisplayName = $("ConfigMgr.RoleDisplayName");

var numArgs = $.Arguments.Count();
// ParseToInt
var siteType   = numArgs > 4 ? parseInt($.Arguments(4)) : undefined;
var hierarchyId = numArgs > 5 ? $.Arguments(5) : undefined;

var targetSiteCode = sccm.SiteCode;

var siteDef, relationship;

// Create SiteInstance
// this siteType come from registry (1:Primary, 2:Secondary, 8:CAS)
switch (siteType) {
   case 1:
      siteDef = data.CreateClassInstance("$MPElement[Name='MECM.PrimarySite']$");
      break;

   case 2:
      siteDef = data.CreateClassInstance("$MPElement[Name='MECM.SecondarySite']$");

      break;
   case 4:
   case 8:
      siteDef = data.CreateClassInstance("$MPElement[Name='MECM.CentralSite']$");
      break;
}
siteDef.AddProperty("$MPElement[Name='MECM.Site']/SiteServer$", sccm.SiteServer);

data.AddInstance(siteDef);

var siteServer = sccm.SiteServer;
var siteWmi = $.Import("WMI").Connect(siteServer, "root\\sms");
var roleName = "ConfigMgr SDK Provider";

siteWmi("SMS_ProviderLocation").Where("SiteCode = '" + targetSiteCode + "'").each(function () {
   var targetServerFqdn = this.Machine;
   var targetServerName = targetServerFqdn;

   if ($.EqualsIgnoreCase(targetServerFqdn, siteServer))
   {
      serverDef = data.CreateClassInstance("$MPElement[Name='MECM.SiteServer']$");
   }
   else
   {
      serverDef = data.CreateClassInstance("$MPElement[Name='MECM.SiteSystemServer']$");

      var serverQuery = $.Format("\
         SELECT res.ServerRemoteName, res.ServerName FROM SMS_SystemResourceList as res \n\
          WHERE res.SiteCode ='{0}' AND res.ServerRemoteName = '{1}' AND res.RoleName ='SMS Site System'", targetSiteCode, targetServerFqdn);
      var isSiteSystem = false;

      // Find all servers
      sccm.Query(serverQuery, function () {
         isSiteSystem = true;
      });

      if (isSiteSystem == false)
      {
         serverDef.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$", targetServerName);
         serverDef.AddProperty("$MPElement[Name='MECM.Server']/HierarchyId$", hierarchyId);
         serverDef.AddProperty("$MPElement[Name='MECM.Server']/SiteCode$", targetSiteCode);
      }
   }

   serverDef.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", targetServerFqdn);

   data.AddInstance(serverDef);

   relationship = data.CreateRelationshipInstance("$MPElement[Name='MECM.SiteContainsServers']$");
   relationship.Source = siteDef;
   relationship.Target = serverDef;

   data.AddInstance(relationship);

   var roleClass = roleClassMap(roleName);
   var displayName = roleDisplayName(roleName);

   var roleDef = data.CreateClassInstance(roleClass);
   roleDef.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", targetServerFqdn);
   roleDef.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$", displayName + " - " + targetServerName);
   roleDef.AddProperty("$MPElement[Name='MECM.SiteRole']/RoleName$", roleName);
   roleDef.AddProperty("$MPElement[Name='MECM.SiteRole']/SiteCode$", targetSiteCode);

   data.AddInstance(roleDef);

   relationship = data.CreateRelationshipInstance("$MPElement[Name='MECM.ServersContainsSiteRoles']$")
   relationship.Source = serverDef;
   relationship.Target = roleDef;

   data.AddInstance(relationship);
});

// END Discovery(MEF $, WMI sccm, DiscoveryData data)
})($, $("ConfigMgr"), $("DiscoveryData"));

(function ($, sccm, data) {
// BEGIN Discovery(MEF $, WMI sccm, DiscoveryData data)

var roleClassMap = $("ConfigMgr.RoleClassMap");

var numArgs = $.Arguments.Count();
// ParseToInt
var siteType   = numArgs > 4 ? parseInt($.Arguments(4)) : undefined;
var parentSiteCode = numArgs > 6 ? $.Arguments(6) : undefined;
var targetSiteCode = sccm.SiteCode;

var siteDef, relationship;

// only available for CAS and Standalone Primary
if ($.IsNullOrEmpty(parentSiteCode) == false)
{
   return;
}

// Create SiteInstance
// this siteType come from registry (1:Primary, 2:Secondary, 8:CAS)
switch (siteType) {
   case 1:
      siteDef = data.CreateClassInstance("$MPElement[Name='MECM.PrimarySite']$");
      break;

   case 2:
      siteDef = data.CreateClassInstance("$MPElement[Name='MECM.SecondarySite']$");

      break;
   case 4:
   case 8:
      siteDef = data.CreateClassInstance("$MPElement[Name='MECM.CentralSite']$");
      break;
}
siteDef.AddProperty("$MPElement[Name='MECM.Site']/SiteServer$", sccm.SiteServer);

// Configuration Baselines
roleQuery = $.Format("\
   SELECT al.Name, al.TypeID, al.TypeInstanceID from SMS_Alert as al \n\
    WHERE al.TypeID = 14 \n\
      AND al.MonitoredByScom = 1 \n\
      AND (al.SourceSiteCode IS NULL OR al.SourceSiteCode='{0}')", targetSiteCode);

sccm.Query(roleQuery, function () {
   var roleDef;
   var typeId = this.TypeID;
   var typeInstanceId = this.TypeInstanceID;
   var alertName = this.Name;

   roleDef = data.CreateClassInstance("$MPElement[Name='MECM.ConfigurationBaseline']$");
   roleDef.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", sccm.SiteServer);
   roleDef.AddProperty("$MPElement[Name='MECM.ConfigurationBaseline']/TypeId$", typeId);
   roleDef.AddProperty("$MPElement[Name='MECM.ConfigurationBaseline']/TypeInstanceId$", typeInstanceId);
   roleDef.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$", alertName);

   data.AddInstance(roleDef);

   relationship = data.CreateRelationshipInstance("$MPElement[Name='MECM.SiteContainsConfigurationBaselines']$")
   relationship.Source = siteDef;
   relationship.Target = roleDef;

   data.AddInstance(relationship);
});

//Alert Discovery from SMS_AlertBase
roleQuery = $.Format("\
   SELECT Name, TypeID, TypeInstanceID, instancenameparam1 from SMS_AlertBase");

sccm.Query(roleQuery, function () {
   var roleDef;
   var typeId = this.TypeID;
   var typeInstanceId = this.TypeInstanceID.replace(/\\/g,"\\\\");  // escape "\\"
   typeInstanceId = typeInstanceId.replace(/\"/g, "\\\"");          // escape "\""
   var alertName = this.Name;
   var instancenameparam1 = this.instancenameparam1;

   switch (typeId){
        //Alert Discovery from SMS_EPAlert
        case 31:
            roleDef = data.CreateClassInstance("$MPElement[Name='MECM.AlertMalwareDetection']$");
            break;
        case 32:
            roleDef = data.CreateClassInstance("$MPElement[Name='MECM.AlertMalwareOutbreak']$");
            break;
        case 33:
            roleDef = data.CreateClassInstance("$MPElement[Name='MECM.AlertRepeatedMalwareDetection']$");
            break;
        case 34:
            roleDef = data.CreateClassInstance("$MPElement[Name='MECM.AlertMultipleMalwareDetection']$");
            break;
        //Alert Discovery from SMS_CHAlert
        case 21:
            roleDef = data.CreateClassInstance("$MPElement[Name='MECM.AlertClientHealthSLA']$");
            break;
        case 22:
            roleDef = data.CreateClassInstance("$MPElement[Name='MECM.AlertClientActivityWarning']$");
            break;
        case 23:
            roleDef = data.CreateClassInstance("$MPElement[Name='MECM.AlertClientRemediationWarning']$");
            break;
        //Alert Discovery from SMS_Alert
        case 9:
            roleDef = data.CreateClassInstance("$MPElement[Name='MECM.AlertSWDAPPSuceessBelowThreshold']$");
            break;
        case 10:
            roleDef = data.CreateClassInstance("$MPElement[Name='MECM.AlertSWDAPPErrorExceedThreshold']$");
            break;
        case 11:
            roleDef = data.CreateClassInstance("$MPElement[Name='MECM.AlertSUM1DeploySuccessBelowThreshold']$");
            break;
        case 12:
            roleDef = data.CreateClassInstance("$MPElement[Name='MECM.AlertSUM2DeploySuccessBelowThreshold']$");
            break;
        case 15:
            roleDef = data.CreateClassInstance("$MPElement[Name='MECM.AlertMigrationInitialSync']$");
            break;
        case 16:
            roleDef = data.CreateClassInstance("$MPElement[Name='MECM.AlertMigrationSyncFailure']$");
            break;
        case 17:
            roleDef = data.CreateClassInstance("$MPElement[Name='MECM.AlertMigrationJobFailure']$");
            break;
        case 20:
            roleDef = data.CreateClassInstance("$MPElement[Name='MECM.AlertExchangeConnector']$");
            break;
        case 35:
            roleDef = data.CreateClassInstance("$MPElement[Name='MECM.AlertOSDTaskSuccessBelowThreshold']$");
            break;
        case 36:
            roleDef = data.CreateClassInstance("$MPElement[Name='MECM.AlertOSDTaskErrorExceedThreshold']$");
            break;
        case 37:
            roleDef = data.CreateClassInstance("$MPElement[Name='MECM.AlertMigrationAMTWarning']$");
            break;
        case 38:
            roleDef = data.CreateClassInstance("$MPElement[Name='MECM.AlertMigrationDPFQDNWarning']$");
            break;
        case 40:
            roleDef = data.CreateClassInstance("$MPElement[Name='MECM.AlertDatabaseReplicationDLLError']$");
            break;
        case 41:
            roleDef = data.CreateClassInstance("$MPElement[Name='MECM.AlertCloudDPStorageWarning']$");
            break;
        case 42:
            roleDef = data.CreateClassInstance("$MPElement[Name='MECM.AlertCloudDPStorageCritical']$");
            break;
        case 43:
            roleDef = data.CreateClassInstance("$MPElement[Name='MECM.AlertCloudDPTrafficOutWarning']$");
            break;
        case 44:
            roleDef = data.CreateClassInstance("$MPElement[Name='MECM.AlertCloudDPTrafficOutCritical']$");
            break;
        default:
            roleDef = null;
   }   
   if(roleDef != null)
   {
        roleDef.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", sccm.SiteServer);
        roleDef.AddProperty("$MPElement[Name='MECM.AlertBaseClass']/TypeId$", typeId);
        roleDef.AddProperty("$MPElement[Name='MECM.AlertBaseClass']/TypeInstanceId$", typeInstanceId);
        if(typeId == 12)
            roleDef.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$", "Low deployment success rate alert of update group: " + instancenameparam1);
        else if(typeId == 20)
            roleDef.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$", "Exchange Server connection failure alert from site: " + instancenameparam1);
        else if(typeId == 40)
            roleDef.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$", "Low deployment success rate alert of update group: " + instancenameparam1);
        else
            roleDef.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$", alertName);

        data.AddInstance(roleDef);

        relationship = data.CreateRelationshipInstance("$MPElement[Name='MECM.SiteContainsConfigurationAlerts']$")
        relationship.Source = siteDef;
        relationship.Target = roleDef;

        data.AddInstance(relationship);
   }


});

// AI Proxy
roleQuery = "SELECT ai.ProxyName, ai.SiteCode from SMS_AIProxy as ai WHERE ai.ProxyEnabled = 1";

sccm.Query(roleQuery, function () {
   var roleDef;
   var name = this.ProxyName;
   var site = this.SiteCode;

   roleDef = data.CreateClassInstance("$MPElement[Name='MECM.AICatalogSynchronization']$");
   roleDef.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", sccm.SiteServer);
   roleDef.AddProperty("$MPElement[Name='MECM.AICatalogSynchronization']/ProxyName$", name);
   roleDef.AddProperty("$MPElement[Name='MECM.AICatalogSynchronization']/SiteCode$", site);
   roleDef.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$", name);

   data.AddInstance(roleDef);

   relationship = data.CreateRelationshipInstance("$MPElement[Name='MECM.SiteContainsAICatalogSynchronization']$")
   relationship.Source = siteDef;
   relationship.Target = roleDef;

   data.AddInstance(relationship);
});

// END Discovery(MEF $, WMI sccm, DiscoveryData data)
})($, $("ConfigMgr"), $("DiscoveryData"));


$.Return($("DiscoveryData"));

WriteAction.ScriptBody:
#================================================================================= # Script to restart a service # # Author: Kevin Holman # # Version: 1.0 # #=================================================================================
# Manual Testing section - put stuff here for manually testing script - typically parameters: #================================================================================= # $ServiceName = "spooler" #=================================================================================
# Constants section - modify stuff here: #================================================================================= # Assign script name variable for use in event logging $ScriptName = "MECM.Client.CcmExec.Service.Recovery.ps1" $EventID = "3750" #=================================================================================
# Starting Script section #================================================================================= # Gather the start time of the script $StartTime = Get-Date #Set variable to be used in logging events $whoami = whoami # Load MOMScript API $momapi = New-Object -comObject MOM.ScriptAPI #=================================================================================
# Begin MAIN script section #=================================================================================
[string]$ServiceName = "CcmExec"
#Log script event that we are starting task $momapi.LogScriptEvent($ScriptName,$EventID,0,"`n Recovery script for service: ($ServiceName) is starting. `n Running as ($whoami).") write-host "Recovery script for service: ($ServiceName) is starting. Running as ($whoami)."
#Get the service object $Svc = Get-Service $ServiceName
#Check to ensure the service exists: IF (!$Svc) { $momapi.LogScriptEvent($ScriptName,3751,2, "`n Service ($ServiceName) was not found on this computer. `n This indicates a misconfiguration of the monitor. `n Error is ($error)") write-host "Service ($ServiceName) was not found on this computer. This indicates a misconfiguration of the monitor. Error is ($error)" EXIT }
#Get Service Status: [string]$SvcStatus = $Svc.Status
#Do not continue is service is already running IF ($SvcStatus -eq "Running") { $momapi.LogScriptEvent($ScriptName,3752,2, "`n Service ($ServiceName) was found to be in the Running state. `n This indicates a misconfiguration of the monitor. `n No recovery action will be taken.") write-host "Service ($ServiceName) was found to be in the Running state. This indicates a misconfiguration of the monitor. No recovery action will be taken." EXIT }
#Log recovery information event $momapi.LogScriptEvent($ScriptName,$EventID,0,"`n Service: ($ServiceName) was found to be in the ($SvcStatus) state. Recovery will be attempted.") write-host "Service: ($ServiceName) was found to be in the ($SvcStatus) state. Recovery will be attempted."
#Attempt First Recovery Restart-Service -Name $ServiceName -Force #Wait time in seconds Start-Sleep 20 #Get the service object $Svc = Get-Service $ServiceName #Get Service Status: $SvcStatus = $Svc.Status
IF ($SvcStatus -ne "Running") { #Attempt Second Recovery $momapi.LogScriptEvent($ScriptName,$EventID,0,"`n Service: ($ServiceName) second attempt at recovering starting now.") write-host "Service: ($ServiceName) second attempt at recovering starting now." Restart-Service -Name $ServiceName -Force Start-Sleep 20 $Svc = Get-Service $ServiceName $SvcStatus = $Svc.Status }
IF ($SvcStatus -ne "Running") { #Attempt Third Recovery $momapi.LogScriptEvent($ScriptName,$EventID,0,"`n Service: ($ServiceName) third attempt at recovering starting now.") write-host "Service: ($ServiceName) third attempt at recovering starting now." Restart-Service -Name $ServiceName -Force Start-Sleep 20 $Svc = Get-Service $ServiceName $SvcStatus = $Svc.Status }
IF ($SvcStatus -eq "Running") { #Log event of a successful restart $momapi.LogScriptEvent($ScriptName,$EventID,0,"`n Service: ($ServiceName) was successfully restarted and then detected to be in the Running state.") write-host "Service: ($ServiceName) was successfully restarted and then detected to be in the Running state." } ELSE { #Log event of a successful restart $momapi.LogScriptEvent($ScriptName,3753,2,"`n Service: ($ServiceName) is not running. `n Recovery was attempted and failed. `n Manual interventon is required. `n The last detected service status was ($SvcStatus).") write-host "Service: ($ServiceName) is not running. Recovery was attempted and failed. Manual interventon is required. The last detected service status was ($SvcStatus)." }
# Check to see if this recovery script has been run multiple times in the last x minutes for detection of too many recovery actions $Message = "*($ServiceName) is starting*" $Events = Get-EventLog -LogName 'Operations Manager' -After $StartTime.AddMinutes(-60) -Message $Message | where {$_.EventID -eq 3750} $EventCount = $Events.Count IF ($EventCount -ge 3) { $momapi.LogScriptEvent($ScriptName,3754,2,"`n Too many service recoveries have been attempted for ($ServiceName). `n ($EventCount) recoveries are detected in the event log. `n Manual interventon is required.") write-host "Too many service recoveries have been attempted for ($ServiceName). ($EventCount) recoveries are detected in the event log. Manual interventon is required." }
# End of script section #================================================================================= #Log an event for script ending and total execution time. $EndTime = Get-Date $ScriptTime = ($EndTime - $StartTime).TotalSeconds $momapi.LogScriptEvent($ScriptName,$EventID,0,"`n Script Completed. `n Script Runtime: ($ScriptTime) seconds.") write-host "Script Completed. Script Runtime: ($ScriptTime) seconds." #================================================================================= # End of script

